const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]


  return (
    <div>
      <Header course={course} />
      <Content parts={parts}/>
      <Total parts={parts}/>
    </div>
  )
}

const Header = (props) => {
  console.log(props)
  return <h1>Hello {props.course}</h1>

}

const Content = (props) => {

  return (
    <div>
      <Part part={props.parts[0]}/>
      <Part part={props.parts[1]}/>
      <Part part={props.parts[2]}/>
    </div>
  )

}

const Total = (props) => {
  console.log(props)


  return (
    <p>Number of exercises {props.parts[0].exercises+props.parts[1].exercises}</p>
  )
}

const Part = (props) => {

  return (
    <p>
        {props.part.name} {props.part.exercises}
    </p>
  )

}

export default App
